pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import 'tapioca-sdk/dist/contracts/libraries/LzLib.sol';
import 'tapioca-sdk/dist/contracts/token/oft/OFT.sol';
import './interfaces/IYieldBox.sol';

//
//                 .(%%%%%%%%%%%%*       *
//             #%%%%%%%%%%%%%%%%%%%%*  ####*
//          #%%%%%%%%%%%%%%%%%%%%%#  /####
//       ,%%%%%%%%%%%%%%%%%%%%%%%   ####.  %
//                                #####
//                              #####
//   #####%#####              *####*  ####%#####*
//  (#########(              #####     ##########.
//  ##########             #####.      .##########
//                       ,####/
//                      #####
//  %%%%%%%%%%        (####.           *%%%%%%%%%#
//  .%%%%%%%%%%     *####(            .%%%%%%%%%%
//   *%%%%%%%%%%   #####             #%%%%%%%%%%
//               (####.
//      ,((((  ,####(          /(((((((((((((
//        *,  #####  ,(((((((((((((((((((((
//          (####   ((((((((((((((((((((/
//         ####*  (((((((((((((((((((
//                     ,**//*,.

abstract contract BaseTOFT is OFT {
    using SafeERC20 for IERC20;
    using BytesLib for bytes;

    /// @notice The YieldBox address.
    IYieldBox public immutable yieldBox;
    /// @notice If this wrapper is for an ERC20 or a native token.
    bool isNative;

    uint16 public constant PT_YB_SEND_STRAT = 770;
    uint16 public constant PT_YB_RETRIEVE_STRAT = 771;
    uint16 public constant PT_YB_DEPOSIT = 772;
    uint16 public constant PT_YB_WITHDRAW = 773;

    /// ==========================
    /// ========== Errors ========
    /// ==========================
    /// @notice Error while depositing ETH assets to YieldBox.
    error TOFT_YB_ETHDeposit();

    /// ==========================
    /// ========== Events ========
    /// ==========================
    event YieldBoxDeposit(uint256 _amount);
    event YieldBoxRetrieval(uint256 _amount);

    constructor(bool _isNative, IYieldBox _yieldBox) {
        yieldBox = _yieldBox;
        isNative = _isNative;
    }

    receive() external payable {}

    // ==========================
    // ========== LZ ============
    // ==========================
    function sendToYB(
        uint256 amount,
        uint256 assetId,
        uint16 lzDstChainId,
        uint256 extraGasLimit,
        address zroPaymentAddress,
        bool strategyDeposit
    ) external payable {
        bytes memory toAddress = abi.encodePacked(msg.sender);
        _debitFrom(msg.sender, lzEndpoint.getChainId(), toAddress, amount);
        bytes memory lzPayload = abi.encode(
            strategyDeposit ? PT_YB_SEND_STRAT : PT_YB_DEPOSIT,
            abi.encodePacked(msg.sender),
            toAddress,
            amount,
            assetId
        );
        bytes memory adapterParam = LzLib.buildDefaultAdapterParams(
            extraGasLimit
        );
        _lzSend(
            lzDstChainId,
            lzPayload,
            payable(msg.sender),
            zroPaymentAddress,
            adapterParam,
            msg.value
        );
        emit SendToChain(lzDstChainId, msg.sender, toAddress, amount);
    }

    function retrieveFromYB(
        uint256 amount,
        uint256 assetId,
        uint16 lzDstChainId,
        address zroPaymentAddress,
        bytes memory airdropAdapterParam,
        bool strategyWithdrawal
    ) external payable {
        bytes memory toAddress = abi.encodePacked(msg.sender);

        bytes memory lzPayload = abi.encode(
            strategyWithdrawal ? PT_YB_RETRIEVE_STRAT : PT_YB_WITHDRAW,
            abi.encodePacked(msg.sender),
            toAddress,
            amount,
            0,
            assetId,
            zroPaymentAddress
        );
        _lzSend(
            lzDstChainId,
            lzPayload,
            payable(msg.sender),
            zroPaymentAddress,
            airdropAdapterParam,
            msg.value
        );
        emit SendToChain(lzDstChainId, msg.sender, toAddress, amount);
    }

    // ================================
    // ========== YieldBox ============
    // ================================

    function _ybDeposit(
        uint16 _srcChainId,
        bytes memory _payload,
        IERC20 _erc20,
        bool _strategyDeposit
    ) internal virtual {
        (
            ,
            //package
            bytes memory fromAddressBytes, //from
            ,
            uint256 amount,
            uint256 assetId
        ) = abi.decode(_payload, (uint16, bytes, bytes, uint256, uint256));

        address onBehalfOf = _strategyDeposit
            ? address(this)
            : fromAddressBytes.toAddress(0);
        _creditTo(_srcChainId, address(this), amount);
        _depositToYieldbox(assetId, amount, _erc20, address(this), onBehalfOf);

        emit ReceiveFromChain(_srcChainId, onBehalfOf, amount);
    }

    function _ybWithdraw(
        uint16 _srcChainId,
        bytes memory _payload,
        bool _strategyWithdrawal
    ) internal virtual {
        (
            ,
            bytes memory from,
            ,
            uint256 _amount,
            uint256 _share,
            uint256 _assetId,
            address _zroPaymentAddress
        ) = abi.decode(
                _payload,
                (uint16, bytes, bytes, uint256, uint256, uint256, address)
            );

        address _from = from.toAddress(0);
        _retrieveFromYieldBox(
            _assetId,
            _amount,
            _share,
            _strategyWithdrawal ? address(this) : _from,
            address(this)
        );

        _debitFrom(
            address(this),
            lzEndpoint.getChainId(),
            abi.encodePacked(address(this)),
            _amount
        );
        bytes memory lzSendBackPayload = abi.encode(PT_SEND, from, _amount);
        _lzSend(
            _srcChainId,
            lzSendBackPayload,
            payable(this),
            _zroPaymentAddress,
            '',
            address(this).balance
        );
        emit SendToChain(
            _srcChainId,
            _from,
            abi.encodePacked(address(this)),
            _amount
        );

        emit ReceiveFromChain(_srcChainId, _from, _amount);
    }

    /// @notice Receive an inter-chain transaction to execute a deposit inside YieldBox.
    function _depositToYieldbox(
        uint256 _assetId,
        uint256 _amount,
        IERC20 _erc20,
        address _from,
        address _to
    ) private {
        _erc20.approve(address(yieldBox), _amount);
        yieldBox.depositAsset(_assetId, _from, _to, _amount, 0);

        emit YieldBoxDeposit(_amount);
    }

    /// @notice Receive an inter-chain transaction to execute a deposit inside YieldBox.
    function _retrieveFromYieldBox(
        uint256 _assetId,
        uint256 _amount,
        uint256 _share,
        address _from,
        address _to
    ) private {
        yieldBox.withdraw(_assetId, _from, _to, _amount, _share);

        emit YieldBoxRetrieval(_amount);
    }
}
","keccak256":"0xbfca185fe95d2172ac96bed42ec2b9e92a75cfaa9f60c25ac8af81b2657d433a","license":"UNLICENSED"},"contracts/TapiocaOFT.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import './TapiocaWrapper.sol';
import './BaseTOFT.sol';

//
//                 .(%%%%%%%%%%%%*       *
//             #%%%%%%%%%%%%%%%%%%%%*  ####*
//          #%%%%%%%%%%%%%%%%%%%%%#  /####
//       ,%%%%%%%%%%%%%%%%%%%%%%%   ####.  %
//                                #####
//                              #####
//   #####%#####              *####*  ####%#####*
//  (#########(              #####     ##########.
//  ##########             #####.      .##########
//                       ,####/
//                      #####
//  %%%%%%%%%%        (####.           *%%%%%%%%%#
//  .%%%%%%%%%%     *####(            .%%%%%%%%%%
//   *%%%%%%%%%%   #####             #%%%%%%%%%%
//               (####.
//      ,((((  ,####(          /(((((((((((((
//        *,  #####  ,(((((((((((((((((((((
//          (####   ((((((((((((((((((((/
//         ####*  (((((((((((((((((((
//                     ,**//*,.

contract TapiocaOFT is BaseTOFT {
    using SafeERC20 for IERC20;
    using BytesLib for bytes;

    /// @notice The TapiocaWrapper contract, owner of this contract.
    TapiocaWrapper public tapiocaWrapper;
    /// @notice Total fees amassed by this contract, in `erc20`.
    uint256 public totalFees;
    /// @notice The ERC20 to wrap.
    IERC20 public immutable erc20;
    /// @notice The host chain ID of the ERC20, will be used only on OP chain.
    uint256 public immutable hostChainID;
    /// @notice Decimal cache number of the ERC20.
    uint8 _decimalCache;

    /// ==========================
    /// ========== Errors ========
    /// ==========================

    /// @notice Code executed not on main chain (optimism/chainID mismatch).
    error TOFT__NotHostChain();
    /// @notice A zero amount was found
    error TOFT_ZeroAmount();

    /// ==========================
    /// ========== Events ========
    /// ==========================
    event Wrap(address indexed _from, address indexed _to, uint256 _amount);
    event Unwrap(address indexed _from, address indexed _to, uint256 _amount);
    event HarvestFees(uint256 _amount);

    constructor(
        address _lzEndpoint,
        bool _isNative,
        IERC20 _erc20,
        IYieldBox _yieldBox,
        string memory _name,
        string memory _symbol,
        uint8 _decimal,
        uint256 _hostChainID
    )
        OFT(
            string(abi.encodePacked('TapiocaOFT-', _name)),
            string(abi.encodePacked('TOFT-', _symbol)),
            _lzEndpoint
        )
        BaseTOFT(_isNative, _yieldBox)
    {
        if (isNative) {
            require(address(_erc20) == address(0), 'TOFT__NotNative');
        }

        erc20 = _erc20;
        _decimalCache = _decimal;
        hostChainID = _hostChainID;

        tapiocaWrapper = TapiocaWrapper(msg.sender);
    }

    /// @notice Require that the caller is on the host chain of the ERC20.
    modifier onlyHostChain() {
        if (block.chainid != hostChainID) {
            revert TOFT__NotHostChain();
        }
        _;
    }

    /// @notice Decimal number of the ERC20
    function decimals() public view override returns (uint8) {
        return _decimalCache;
    }

    /// @notice Return the output amount of an ERC20 token wrap operation.
    function wrappedAmount(uint256 _amount) public view returns (uint256) {
        return
            _amount -
            estimateFees(
                tapiocaWrapper.mngmtFee(),
                tapiocaWrapper.mngmtFeeFraction(),
                _amount
            );
    }

    /// @notice Wrap an ERC20 with a 1:1 ratio with a fee if existing.
    /// @dev Since it can be executed only on the main chain, if an address exists on the OP chain it will not allowed to wrap.
    /// @param _toAddress The address to wrap the ERC20 to.
    /// @param _amount The amount of ERC20 to wrap.
    function wrap(address _toAddress, uint256 _amount) external onlyHostChain {
        uint256 mngmtFee = tapiocaWrapper.mngmtFee();

        if (mngmtFee > 0) {
            uint256 feeAmount = estimateFees(
                mngmtFee,
                tapiocaWrapper.mngmtFeeFraction(),
                _amount
            );

            totalFees += feeAmount;
            erc20.safeTransferFrom(
                msg.sender,
                address(this),
                _amount + feeAmount
            );
        } else {
            erc20.safeTransferFrom(msg.sender, address(this), _amount);
        }

        _mint(_toAddress, _amount);
        emit Wrap(msg.sender, _toAddress, _amount);
    }

    /// @notice Wrap a native token with a 1:1 ratio with a fee if existing.
    /// @dev Since it can be executed only on the host chain, if an address exists on the linked chain it will not allowed to wrap.
    /// @param _toAddress The address to wrap the tokens to.
    function wrapNative(address _toAddress) external payable onlyHostChain {
        if (msg.value == 0) {
            revert TOFT_ZeroAmount();
        }

        uint256 toMint;
        uint256 mngmtFee = tapiocaWrapper.mngmtFee();

        if (mngmtFee > 0) {
            uint256 feeAmount = estimateFees(
                mngmtFee,
                tapiocaWrapper.mngmtFeeFraction(),
                msg.value
            );

            totalFees += feeAmount;
            toMint = msg.value - feeAmount;
        }

        _mint(_toAddress, toMint);
        emit Wrap(msg.sender, _toAddress, toMint);
    }

    /// @notice Harvest the fees collected by the contract. Called only on host chain.
    function harvestFees() external onlyHostChain {
        erc20.safeTransfer(address(tapiocaWrapper.owner()), totalFees);
        totalFees = 0;
        emit HarvestFees(totalFees);
    }

    /// @notice Unwrap an ERC20/Native with a 1:1 ratio. Called only on host chain.
    /// @param _toAddress The address to unwrap the tokens to.
    /// @param _amount The amount of tokens to unwrap.
    function unwrap(address _toAddress, uint256 _amount)
        external
        onlyHostChain
    {
        _burn(msg.sender, _amount);

        if (isNative) {
            safeTransferETH(_toAddress, _amount);
        } else {
            erc20.safeTransfer(_toAddress, _amount);
        }

        emit Unwrap(msg.sender, _toAddress, _amount);
    }

    // ================================
    // ========== INTERNAL ============
    // ================================

    /// @notice Estimate the management fees for a wrap operation.
    function estimateFees(
        uint256 _feeBps,
        uint256 _feeFraction,
        uint256 _amount
    ) public pure returns (uint256) {
        return (_amount * _feeBps) / _feeFraction;
    }

    /// @notice Author: Solmate (https://github.com/transmissions11/solmate/blob/main/src/utils/SafeTransferLib.sol)
    function safeTransferETH(address to, uint256 amount) internal {
        bool success;

        assembly {
            // Transfer the ETH and store if it succeeded or not.
            success := call(gas(), to, amount, 0, 0, 0, 0)
        }

        require(success, 'ETH_TRANSFER_FAILED');
    }

    // ==========================
    // ========== LZ ============
    // ==========================

    function _nonblockingLzReceive(
        uint16 _srcChainId,
        bytes memory _srcAddress,
        uint64 _nonce,
        bytes memory _payload
    ) internal virtual override {
        uint16 packetType;
        assembly {
            packetType := mload(add(_payload, 32))
        }

        if (packetType == PT_SEND) {
            _sendAck(_srcChainId, _srcAddress, _nonce, _payload);
        } else if (packetType == PT_YB_SEND_STRAT) {
            _ybDeposit(_srcChainId, _payload, IERC20(address(this)), true);
        } else if (packetType == PT_YB_RETRIEVE_STRAT) {
            _ybWithdraw(_srcChainId, _payload, true);
        } else if (packetType == PT_YB_DEPOSIT) {
            _ybDeposit(_srcChainId, _payload, IERC20(address(this)), false);
        } else if (packetType == PT_YB_WITHDRAW) {
            _ybWithdraw(_srcChainId, _payload, false);
        } else {
            revert('OFTCore: unknown packet type');
        }
    }

    /// @notice Check if the current chain is the host chain of the ERC20.
    function isHostChain() external view returns (bool) {
        return block.chainid == hostChainID;
    }

    function getLzChainId() external view returns (uint16) {
        return lzEndpoint.getChainId();
    }
}
","keccak256":"0x181ccc26bd352b01eaff4c9e6fd6c175a752b3fa6eb5f4c4b86c16ce2a96d95b","license":"UNLICENSED"},"contracts/TapiocaWrapper.sol":{"content":"// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.0;
import './TapiocaOFT.sol';

import '@openzeppelin/contracts/utils/Create2.sol';
import '@rari-capital/solmate/src/auth/Owned.sol';

contract TapiocaWrapper is Owned {
    /// @notice Management fee for a wrap operation. In BPS.
    uint256 public mngmtFee;
    /// @notice Denominator for `mngmtFee`.
    uint256 public constant mngmtFeeFraction = 10000;

    /// @notice Array of deployed TOFT contracts.
    TapiocaOFT[] public tapiocaOFTs;
    /// @notice Array of harvestable TOFT fees.
    TapiocaOFT[] private harvestableTapiocaOFTs;
    /// @notice Map of deployed TOFT contracts by ERC20.
    mapping(address => TapiocaOFT) public tapiocaOFTsByErc20;

    /// ==========================
    /// ========== Events ========
    /// ==========================

    /// @notice Called when a new OFT is deployed.
    event CreateOFT(TapiocaOFT indexed _tapiocaOFT, address indexed _erc20);
    /// @notice Called when fees are harvested.
    event HarvestFees(address indexed _caller);
    /// @notice Called when fees are changed.
    event SetFees(uint256 _newFee);

    /// ==========================
    /// ========== Errors ========
    /// ==========================

    /// @notice If the TOFT is already deployed.
    error TapiocaWrapper__AlreadyDeployed(address _erc20);
    /// @notice Failed to deploy the TapiocaWrapper contract.
    error TapiocaWrapper__FailedDeploy();
    /// @notice The management fee is too high. Currently set to a max of 50 BPS or 0.5%.
    error TapiocaWrapper__MngmtFeeTooHigh();
    /// @notice The TapiocaOFT execution failed.
    error TapiocaWrapper__TOFTExecutionFailed(bytes message);
    /// @notice No TOFT has been deployed yet.
    error TapiocaWrapper__NoTOFTDeployed();

    constructor() Owned(msg.sender) {}

    /// ==========================
    /// ========== TOFT ==========
    /// ==========================

    /// @notice Return the number of TOFT contracts deployed on the current chain.
    function tapiocaOFTLength() external view returns (uint256) {
        return tapiocaOFTs.length;
    }

    /// @notice Return the number of harvestable TOFT contracts deployed on the current chain.
    function harvestableTapiocaOFTsLength() external view returns (uint256) {
        return harvestableTapiocaOFTs.length;
    }

    /// @notice Return the latest TOFT contract deployed on the current chain.
    function lastTOFT() external view returns (TapiocaOFT) {
        if (tapiocaOFTs.length == 0) {
            revert TapiocaWrapper__NoTOFTDeployed();
        }
        return tapiocaOFTs[tapiocaOFTs.length - 1];
    }

    /// ================================
    /// ========== Management ==========
    /// ================================

    /// @notice Deploy a new TOFT contract. Callable only by the owner.
    /// @param _erc20 The ERC20 to wrap.
    /// @param _bytecode The executable bytecode of the TOFT contract.
    /// @param _salt Create2 salt.
    function createTOFT(
        address _erc20,
        bytes calldata _bytecode,
        bytes32 _salt
    ) external onlyOwner {
        if (address(tapiocaOFTsByErc20[_erc20]) != address(0x0)) {
            revert TapiocaWrapper__AlreadyDeployed(_erc20);
        }

        TapiocaOFT toft = TapiocaOFT(
            payable(
                Create2.deploy(
                    0,
                    keccak256(
                        abi.encodePacked(
                            keccak256('TapiocaWrapper'),
                            address(this),
                            _erc20,
                            _salt
                        )
                    ),
                    _bytecode
                )
            )
        );
        if (address(toft.erc20()) != _erc20) {
            revert TapiocaWrapper__FailedDeploy();
        }

        tapiocaOFTs.push(toft);
        tapiocaOFTsByErc20[_erc20] = toft;

        if (toft.isHostChain()) {
            harvestableTapiocaOFTs.push(toft);
        }
        emit CreateOFT(toft, _erc20);
    }

    /// @notice Harvest fees from all the deployed TOFT contracts. Fees are transferred to the owner.
    function harvestFees() external {
        for (uint256 i = 0; i < harvestableTapiocaOFTs.length; i++) {
            harvestableTapiocaOFTs[i].harvestFees();
        }
        emit HarvestFees(msg.sender);
    }

    /// @notice Set the management fee for a wrap operation.
    /// @custom:invariant Forbid a management fee higher than 0.5%.
    /// @param _mngmtFee The new management fee for a wrap operation. In BPS.
    function setMngmtFee(uint256 _mngmtFee) external onlyOwner {
        if (_mngmtFee > 50) {
            revert TapiocaWrapper__MngmtFeeTooHigh();
        }

        mngmtFee = _mngmtFee;
        emit SetFees(mngmtFee);
    }

    /// @notice Execute the `_bytecode` against the `_toft`. Callable only by the owner.
    /// @dev Used to call derived OFT functions to a TOFT contract.
    /// @param _toft The TOFT contract to execute against.
    /// @param _bytecode The executable bytecode of the TOFT contract.
    /// @param _revertOnFailure Whether to revert on failure.
    /// @return success If the execution was successful.
    /// @return result The error message if the execution failed.
    function executeTOFT(
        address _toft,
        bytes calldata _bytecode,
        bool _revertOnFailure
    ) external payable onlyOwner returns (bool success, bytes memory result) {
        (success, result) = payable(_toft).call{value: msg.value}(_bytecode);
        if (_revertOnFailure && !success) {
            revert TapiocaWrapper__TOFTExecutionFailed(result);
        }
    }
}
